package com.tmoncorp.directdeal.api.domain.actions;

import com.tmoncorp.core.rpc.client.RpcResponse;
import com.tmoncorp.core.rpc.client.RpcResponseException;
import com.tmoncorp.core.rpc.client.RpcRestClient;
import com.tmoncorp.directdeal.api.domain.RpcLog;
import com.tmoncorp.directdeal.api.domain.core.Deal;
import com.tmoncorp.directdeal.api.domain.enums.YesNoType;
import com.tmoncorp.directdeal.api.domain.event.DealEvent;
import com.tmoncorp.directdeal.api.repository.PartnerRepository;
import com.tmoncorp.directdeal.api.service.NotificationService;
import com.tmoncorp.module.jandi.model.JandiMessage;
import com.tmoncorp.mvc.security.user.AbstractUser;
import com.tmoncorp.support.util.OptionalConsumer;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Async;
import org.springframework.stereotype.Component;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * SetCouponAgreeLogAction.java version 2016, 07. 12
 * <p>
 * Copyright 2016 Ticketmonster Corp. All rights Reserved.
 * Ticketmonster PROPRIETARY/CONFIDENTIAL. Use is subject to license terms.
 *
 * 할인쿠폰 과세표준 동의 여부 저장
 * wiki : https://wiki.tmon.co.kr/pages/viewpage.action?pageId=46170972
 * jira : SOA-165
 */
@Slf4j
@Component(ApiMethods.COUPON_AGREE_YN)
public class SetCouponAgreeLogAction extends DirectDealActionBase implements ActionRunnable {
	@Autowired
	private RpcRestClient rpcRestClient;

	@Autowired
	private NotificationService notificationService;

	@Autowired
	private PartnerRepository partnerRepository;

	@Async(value = "api")
	@Override
	public void runAction(DealEvent event) {
		log.debug("========== Start SetCouponAgreeLogAction ==========");

		try {
			Map reqParams = makeRequestParams(event);
			RpcResponse&gt; rpcResult = rpcRestClient
					.postMapWithResponse(ApiServer.ITB_RPC, ApiMethods.COUPON_AGREE_YN, Object.class, reqParams);
			saveRpcLog(new RpcLog(ApiServer.ITB_RPC, this, ApiMethods.COUPON_AGREE_YN, reqParams, rpcResult.getResult()));
		} catch (RpcResponseException e) {
			final long dealNo = ((Deal) event.getPayload().get(0)).getDealNo();
			log.error("[SetCouponAgreeLogAction] dealNo: {}", dealNo, e);
			notificationService.sendMessageToJandi(ApiMethods.COUPON_AGREE_YN,
					JandiMessage.ConnectInfo.make("Failed to api call", "Deal No: " + dealNo, null));
		}

		log.debug("========== End SetCouponAgreeLogAction ==========");
	}

	private Map makeRequestParams(DealEvent event) {
		List payload = event.getPayload();
		final Deal deal = (Deal) payload.get(0);
		final AbstractUser user = (AbstractUser) payload.get(1);

		final long partnerNo = deal.getPartnerNo();

		Map paramMap = new HashMap&lt;&gt;();
		paramMap.put("main_deal_srl", deal.getDealNo());
		paramMap.put("partner_srl", partnerNo);

		// SOA-300 과세표준 예외 파트너 처리 추가
		OptionalConsumer.of(partnerRepository.findPartnerByNo((int) partnerNo)
				.filter(p -&gt; p.getTaxtnExceptYn().equals(YesNoType.YES)))
				.ifPresent(p -&gt; { // 과세표준 예외파트너 이면
					paramMap.put("coupon_agree_yn", YesNoType.NO.toString());  //무조건 coupon_agree_yn은 N으로
					paramMap.put("dc_apply_yn", YesNoType.YES.toString());     //무조건 dc_apply_yn은 Y로
				})
				.ifNotPresent(() -&gt; { // 과세표준 예외파트너가 아니면 화면에서 받은 딜속성 값대로 처리
					paramMap.put("coupon_agree_yn", deal.getIsTaxBaseCoupon().toString());
					// dc_apply_yn 값을 안보내주면 coupon_agree_yn과 동일하게 따른다고 함
				});

		paramMap.put("creator", "SYSTEM"); // 딜 생성/복제 시에만 호출되므로 SYSTEM 고정
		paramMap.put("creator_ip", user.getIp());
		return paramMap;
	}
}</p>

