    try {
        // QR Code Details
        std::string qrText = "https://example.com"; // Text to encode in QR code
        int qrVersion = 1; // Version of the QR code (1-40)
        QRecLevel qrEcc = QR_ECLEVEL_L; // Error correction level

        // Image Dimensions
        int width = 200, height = 250; // Adjust as needed
        int qrSize = 150; // Size of the QR code within the image
        int qrMargin = (width - qrSize) / 2; // Center the QR code horizontally
        int textY = qrSize + 20; // Vertical position of the text

        // Generate QR Code
        QRcode* qrCode = QRcode_encodeString(qrText.c_str(), qrVersion, qrEcc, QR_MODE_8, 1);
        if (qrCode == NULL) {
            throw std::runtime_error("Failed to generate QR code.");
        }

        // Create Cairo Surface and Context
        cairo_surface_t* surface = cairo_image_surface_create(CAIRO_FORMAT_ARGB32, width, height);
        if (cairo_surface_status(surface) != CAIRO_STATUS_SUCCESS) {
            QRcode_free(qrCode);
            throw std::runtime_error("Failed to create Cairo surface.");
        }
        cairo_t* cr = cairo_create(surface);
        if (cairo_status(cr) != CAIRO_STATUS_SUCCESS) {
            cairo_surface_destroy(surface);
            QRcode_free(qrCode);
            throw std::runtime_error("Failed to create Cairo context.");
        }

        // Draw QR Code
        for (int y = 0; y < qrCode->width; y++) {
